import fetcher from '../fetcher';

const getApartment = async () => {
    try{
        const {data} = await fetcher.get("/apartments");
        return data;
    }catch(error){
        throw new Error(`Cant get apartments`);
    }
}

const getFilterApartments = (handleSuccess, query) => {
    axios.get(`http://localhost:8000/apartments/${query}`)
    .then(result => handleSuccess(result.data))
}



export {getApartment, getFilterApartments};








