import React from 'react';
import {getCountries} from '../../data/countries'

class ApartmentFilter extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            countries: '', 
        };
    }

    handleChange = (e) => {
        e.preventDefault();
        let {name, value} = e.target;
        if(name === "country"){         
            this.filterCity(value);
        } else {
            console.log("name", name)
            console.log("value", value)
            this.setState({
                [name]: value,
            }, () => {
                this.createQuery();
            });
        }
    };

    async componentDidMount() {
        try{
            const countries = await getCountries();
            this.setState({ 
                countries
            });
        }catch(error){
            console.log(error.message)
        }
    };

    render() {
        const {handleChange, cities} = this.props;
        const {countries} = this.state;
        return (
            <main className={"sticky-top navbar-light main container-fluid"} style={{backgroundColor: "white"}}>
                <div id={"filter-gallery"} className={"row align-items-center"}>


                    <div className={"col-4 col-md-6"} style={inputWrapper} id="address">
                        <select id="inputState" className="form-control" name={"country"} onChange={handleChange}>
                                <option defaultValue>Country</option>
                                {/* {countries.length !== 0 &&
                                countries.map((country, i) => 
                                    <option value={country.id} key={i}>{country.name}</option> */}
                                )}
                        </select>
                    </div>




                    {/* <div className={"d-none d-md-inline"} id="address">
                        <select id="inputState" className="form-control" name={"country"} onChange={handleChange}>
                            <option defaultValue>Country</option>
                            {countries.length !== 0 &&
                            countries.map((country, i) => 
                                <option value={country.id} key={i}>{country.name}</option>
                            )}
                        </select>
                    </div> */}
                    {/* {cities.length !== 0 &&
                    <div className={"d-none d-md-inline"} id="address">
                        <select id="inputState" className="form-control" name={"city"} onChange={handleChange}>
                            <option defaultValue>City</option>
                            {cities.length !== 0 &&
                            cities.map((city, i) => 
                                <option value={city.id} key={i}>{city.name}</option>
                            )}
                        </select>
                    </div>
                    } */}
                </div>
            </main>
        );
    }
}

export default ApartmentFilter;
