import React from 'react';
import {getCountries} from '../../data/countries'
import {getCitiesByCountry} from '../../data/citiesByCountry'
class ApartmentFilter extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            countries: '', 
        };
    } 

    async filterCity(id){
        try{
            const cities = await getCitiesByCountry(id)
            this.setState({
                cities
            });
        }catch(error){
            console.log(error)
        }
    }

    async componentDidMount() {
        try{
            const countries = await getCountries();
            this.setState({ 
                countries
            });
        }catch(error){
            console.log(error.message)
        }
    };

    handleChange = (e) => {
        e.preventDefault();
        
        let {name, value} = e.target;
        console.log("name", name)
        console.log("value", value)

        if(name === "country"){         
            this.filterCity(value);
        } else {
            console.log("name", name)
            console.log("value", value)
            this.setState({
                [name]: value,
            });
        }
    };


    render() {
        const {handleChange, cities} = this.props;
        const inputWrapper = { paddingTop: "20px" };
        const inputStyle = {borderRadius: "5px", width: "90%", fontSize: "15px"};
        const {countries} = this.state;

        console.log("cities", cities)
        return (
            <div>
                <div className={"col-4 col-md-6"} style={inputWrapper} id="address">
                    <select style={inputStyle} id="inputState" className="form-control" name={"country"} onChange={handleChange}>
                            <option defaultValue>Country</option>
                            {countries.length !== 0 &&
                            countries.map((country, i) => 
                                <option value={country.id} key={i}>{country.name}</option>
                            )}
                    </select>
                </div>
                <div className={"col-4 col-md-6"} style={inputWrapper} id="address">
                    <select style={inputStyle}  id="inputState" className="form-control" name={"city"} onChange={handleChange}>
                        <option defaultValue>City</option>
                        {/* {cities.length !== 0 &&
                        cities.map((city, i) => 
                            <option value={city.id} key={i}>{city.name}</option>
                        )} */}
                    </select>
                </div>
            </div>
        );
    }
}

export default ApartmentFilter;
