import React from 'react';
import {Link} from "react-router-dom";
import Cookies from 'js-cookie'

import  './style/usermenu.css';

class UserMenu extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            user: {} ,
            user_id:''
        };
    }

    componentDidMount(){
        let user = Cookies.get();
        if (Object.entries(user).length !== 0){
            user = JSON.parse(user.auth);
            this.setState({
                user,
                user_id: user.id
            })
        }
    }
    

    logOut = () =>{
        console.log("clicked")
        Cookies.remove('auth', {path:'/'});
        this.setState({
            user: {},
            user_id: ''
        })
    }

    render() {
        const {user, user_id} = this.state;
        const hrStyle = {color: "lightgray", height: 1, width: "120px"}
        return (
            <div>
                {Object.entries(user).length !== 0 ? 
                    <ul className={"nav-bar-list nav-bar d-flex"}>
                        <li  className={"d-flex "}>
                            <img alt={"preson-icon"} src="https://img.icons8.com/pastel-glyph/64/000000/christmas-boy--v1.png" style={{width:"30px"}}></img>
                            <div className={"hidden-middle-bar"}>
                                <p>Wellcome {user.first_name}!</p>
                                <div className={"middle-bar"}>
                                    <div className={"middle-bar-img"}>
                                    <hr style={hrStyle}/>
                                        <ul>
                                            <li>
                                                <Link to="/addapartment">
                                                    <h6>Add-Apartment</h6>
                                                </Link>
                                            </li>
                                        </ul>
                                    </div>
                                </div>
                                <div className={"middle-bar"}>
                                    <div className={"middle-bar-img"}>
                                    <hr style={hrStyle}/>
                                        <ul>
                                            <li>
                                                <Link to={`/myapartments/${user_id}`}>
                                                    <h6>My Apartments</h6>
                                                </Link>
                                            </li>
                                        </ul>
                                    </div>
                                </div>
                                <div className={"middle-bar"}>
                                    <div className={"middle-bar-img"}>
                                    <hr style={hrStyle}/>
                                        <ul>
                                            <li onClick={this.logOut}>
                                                <p >Log out</p>
                                            </li>
                                        </ul>
                                    </div>
                                </div>
                            </div>
                        </li>
                    </ul>
                    :
                    <ul className={"nav-bar-list nav-bar d-flex"}>
                        <li>
                            <Link to="/login" className={"no-underline"}>
                                <p>Log in</p>
                                <div className={"hidden-right-bar"}>
                                    <p>Log in to your account</p>
                                </div>
                            </Link>
                        </li>
                        <li>
                        <Link to="/signup" className={"no-underline"}>
                            <p>Sign up</p>
                            <div className={"hidden-right-bar"}>
                                <p>Create a free account with realtor.com</p>
                            </div>
                            </Link>
                        </li>
                    </ul>
                    }
                </div>
        )
    }
}

export default UserMenu;
