    constructor(props) {
        super(props);
        this.state = {
            countries: '', 
        };
    }

    handleChange = (e) => {
        e.preventDefault();
        let {name, value} = e.target;
        if(name === "country"){         
            this.filterCity(value);
        } else {
            console.log("name", name)
            console.log("value", value)
            this.setState({
                [name]: value,
            }, () => {
                this.createQuery();
            });
        }
    };

