import React from 'react';
import BuildApartment from "./buildApartment";
import Form from "../forms/form";
import {getFilterApartments} from '../../data/apartments'
import {getCountriesByCountry} from '../../data/citiesByCountry'

class Gallery extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            apartmentArray: this.props.apartmentArray,
            city: '',
            price: '',
            country: '',
            address: '',
            number_of_bath: '',
            number_of_room: '',
            listingStatus: '',
        };
    }

    createQuery() {
        const {city, price, number_of_bath, number_of_room, listingStatus} = this.state;
        let query = '?';
        console.log("got here")
        if (city){
            console.log("im city here")
            if(city.length !== 0){
                console.log("city", city)
                console.log("inside city query before", query)
                query += `city=${city}&` 
                console.log("inside city query after", query)
            }
        }
        if (price) {
            query += `price=${price}&` 
        }
        if (number_of_bath) {
            query += `number_of_bath=${number_of_bath}&` 
        }
        if (number_of_room) {
            query += `number_of_room=${number_of_room}&` 
        }
        if (listingStatus) {
            query += `listingStatus=${listingStatus}&` 
        }
        this.filterApartment(query);
    };
    async filtetCity(id){
        try{
            const city = await getCountriesByCountry(id)
            this.setState({
                city
            });
        }catch(error){
            console.log(error)
        }
    }
    async filterApartment(query){
        try {
            const apartmentArray = await getFilterApartments(query);
            this.setState({ 
                apartmentArray
            });
        }catch(error){
            console.log(error)
        }
    }

    handleChange = (e) => {
        e.preventDefault();
        let {name, value} = e.target;
        if(name === "country"){
            console.log("country value", value)
            console.log("country name",name)            
            this.filtetCity(value);
        } else {
            this.setState({
                [name]: value.toLowerCase(),
            }, () => {
                this.createQuery();
            }, console.log("why you crash?"));
        }
    };

    render() {
        // console.log("country", this.state.country)
        console.log("city", this.state.city)
        console.log("price1", this.state.price.substr(0, this.state.price.indexOf('-')))
        console.log("price2", this.state.price)
        console.log("apartmentArray", this.state.apartmentArray)


        return (
            <div className={"row justify-content-between wrap-img-div"}>
                <Form handleChange={this.handleChange}
                        city={this.state.city}/>
                {this.state.apartmentArray.length !== 0 &&
                        <p style={{marginLeft:"20px",width:"-webkit-fill-available",color: "gray",fontSize: "14px"}}>
                            Currnetly apartments are only available in Israel 
                            <br/>{this.state.apartmentArray.length} Homes</p>}
                {this.state.apartmentArray.map((apartment, i) =>
                    <BuildApartment {...apartment} key={i}/>
                )}
            </div>
        );
    }
}

export default Gallery;
