import React from 'react';
import BuildApartment from "./buildApartment";
import Form from "../forms/form";
import {getFilterApartments} from '../../data/getData/getApartments'

class Gallery extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            apartmentArray: this.props.apartmentArray,
            city: '',
            price: '',
            address: '',
            number_of_bath: '',
            number_of_room: '',
            listingStatus: '',
            stateQuery:''
        };
    }

    // searchApartmentByCity = () => {
    //     let newArray = this.state.arrayCity;
    //     if (this.state.city) {
    //         newArray = newArray.filter(city => city["label"].toLowerCase().includes(this.state.city));
    //         console.log("list", newArray);
    //     }
    //     this.setState({apartmentArray: newArray});
    // };

    createQuery() {
        const {address, price, number_of_bath, number_of_room, listingStatus} = this.state;
        let query = '?';
        if (address) {
            query += `address=${address}&` 
        }
        if (price) {
            query += `price=${price}&` 
        }
        if (number_of_bath) {
            query += `number_of_bath=${number_of_bath}&` 
        }
        if (number_of_room) {
            query += `number_of_room=${number_of_room}&` 
        }
        if (listingStatus) {
            query += `listingStatus=${listingStatus}&` 
        }

        this.filterApartment(query);
        // this.setState({apartmentArray: newList});
    };

    filterApartment(query){
        getFilterApartments(this.handlefilterSuccess, query)
    }

    handlefilterSuccess = (data) =>{
        console.log("filter data here", data)
        // this.setState({ 
        //     apartmentArray: data
        // });
    }

    handleChange = (e) => {
        e.preventDefault();
        let {name, value} = e.target;
        this.setState({
            [name]: value.toLowerCase(),
        }, () => {
            this.createQuery();
        });
    };

    onSubmit = (e) => {
        e.preventDefault();
    };

    render() {
        console.log("this.state.number_of_baths", this.state.number_of_baths)
        return (
            <div className={"row justify-content-between wrap-img-div"}>
                <Form onSubmit={this.onSubmit}
                      handleChange={this.handleChange}
                />
                {/* {this.state.apartmentArray.map((apartment, i) =>
                    <BuildApartment {...apartment} key={i}/>
                )} */}
            </div>
        );
    }
}

export default Gallery;
